uint32_t
bcm56613_a0_blockport_addr(int block, int port, uint32_t offset)
{
    if (block & 0x10) {
        block &= ~0x10;
        block |= 0x400;
    }
    if (block & 0x20) {
        block &= ~0x20;
        block |= 0x800;
    }
    return ((block * 0x100000) | (port * 0x1000) | (offset & ~0xf00000)); 
}

